var escapeCharacters = { '\\': '\\\\', '"': '\\"', '\t': '\\t',
                        '\n': '\\n', '\r': '\\r', '\b': '\\b', '\f': '\\f' };

var RDFConverter = function(uri, prefix) {
    this.subjects = {};
    this.uri = uri;
    this.prefix = prefix;
}


// add Triple where object is a URI
RDFConverter.prototype.writeTripleObjectUri = function(subject, predicate, objectUri) {
    var subjectStr = this.escapeString(subject);

    if (!(subjectStr in this.subjects)) {
        this.subjects[subjectStr] = [];    
    }

    this.subjects[subjectStr].push({
        predicate: this.escapeString(predicate),
        object: {
            object: this.escapeString(objectUri),
            type: 'uri'
        }
    });
}


RDFConverter.prototype.getRDFTriples = function() {
    return this.subjects;
}


// add Triple where object is a string
RDFConverter.prototype.writeTripleObjectString = function(subject, predicate, objectString) {
    var subjectStr = this.escapeString(subject);

    if (!(subjectStr in this.subjects)) {
        this.subjects[subjectStr] = [];    
    }

    this.subjects[subjectStr].push({
        predicate: this.escapeString(predicate),
        object: {
            object: this.escapeString(objectString),
            type: 'string'
        }
    });
}


// add Triple where object is plain
RDFConverter.prototype.writeTripleObjectPlain = function(subject, predicate, object) {
    var subjectStr = this.escapeString(subject);

    if (!(subjectStr in this.subjects)) {
        this.subjects[subjectStr] = [];    
    }

    this.subjects[subjectStr].push({
        predicate: this.escapeString(predicate),
        object: {
            object: this.escapeString(object),
            type: 'plain'
        }
    });
}


// export as n3 string
RDFConverter.prototype.exportN3 = function() {
    var subjectsKeys = Object.keys(this.subjects);
    var rdfString = '';

    rdfString += this.prefix + '\n';

    for (var i = 0; i < subjectsKeys.length; i++) {
        var currentSubject = this.subjects[subjectsKeys[i]];
        rdfString += '<' + this.uri + subjectsKeys[i] + '> ';

        for (var x = 0; x < currentSubject.length; x++) {
            rdfString += currentSubject[x].predicate + ' ';
            var obj = currentSubject[x].object;
            
            if (obj.type == 'uri')
                rdfString += '<' + obj.object + '>';
            else if (obj.type == 'string')
                rdfString += '"' + obj.object + '"';
            else 
                rdfString += obj.object; 

            if (x < currentSubject.length - 1)
                rdfString += ';\n\t';
            else
                rdfString += '.\n';
        }
        
        rdfString += '\n';
    }

    return rdfString;
}


RDFConverter.prototype.escapeString = function(str) {
    var escapeKeys = Object.keys(escapeCharacters);

    for (var i = 0; i < escapeKeys.length; i++) {
        str = str.replace(escapeKeys[i], escapeCharacters[escapeKeys[i]]);
    }

    return str;
}

var WAMVocabulary = function() {
    this.namespace = 'wam';
    this.label = this.namespace + ':label';
    this.trustRelationship = this.namespace + ':trusts';
    this.invocation = this.namespace +':invokes';
    this.legacyRelationship = this.namespace + ':hasLegacyRelationshipTo';
    this.application = this.namespace + ':Application';
    this.identityProvider = this.namespace + ':IdentityProvider';
    this.processUnit = this.namespace + ':ProcessUnit';
    this.securityRealm = this.namespace + ':SecurityRealm';
    this.service = this.namespace + ':Service';
    this.dataProvider = this.namespace + ':DataProvider';
    this.contains = this.namespace + ':contains';
};

var RDFSVocabulary = function() {
    this.namespace = 'rdfs';
    this.label = this.namespace + ':label';
    this.seeAlso = this.namespace + ':seeAlso';
}


WAMGraph = joint.dia.Graph.extend({
    currentElement: null,
    rdfConverter: RDFConverter,

    initialize: function() {
        joint.dia.Graph.prototype.initialize.apply(this, arguments);
    },

    // create graph from RDF
    fromRDF: function(data, callback) {
        var parser = N3.Parser();
        this.clear();
        var curElem = null;
        var self = this;

        var converter = new this.rdfConverter('', '');

        parser.parse(data, function (error, triple, prefixes) {
            // parse and store triples
            if (triple) {
                var predicate = triple.predicate.split('#')[1];
                var obj = triple.object.split('#')[1];

                if (!obj || predicate == 'seeAlso')
                    obj = triple.object;
        
                var subject = triple.subject.split('#')[1];

                converter.writeTripleObjectPlain(subject, predicate, obj);
            }
            else if (error)
                 console.log(error);
            else {
                // after parsing is finished create graph elements
                var createdCells = {};
                var ns = new WAMVocabulary().namespace;

                var rdfTriples = converter.getRDFTriples();

                _.each(rdfTriples, function(subj, subjKey) {
                    _.each(subj, function(triple) {
                        if (triple.predicate == "type") {
                            if (WAMOntology[ns + ':' + triple.object.object]) {
                                var elem = new WAMOntology[ns + ':' + triple.object.object].element({});
                                elem.cid = 'c' + subjKey.replace(elem.get('key'), '');
                                self.addCell(elem);
                                createdCells[subjKey] = elem.id;
                            }
                        }
                    });
                });

                // create links, add labels and embed child elements
                _.each(rdfTriples, function(subj, subjKey) {
                    _.each(subj, function(triple) {
                        switch (triple.predicate) {
                            case 'type': break;
                            case 'contains':
                                var parentElement = self.getCell(createdCells[subjKey]);
                                var childID = triple.object.object;
                                var childElement = self.getCell(createdCells[childID]);
                                childElement.toFront({deep: true});
                                parentElement.embed(childElement);
                                break;
                            case 'invokes':                                
                            case 'hasLegacyRelationshipTo':
                            case 'trusts':
                                var curLink = new WAMOntology[ns + ':' + triple.predicate].element({});
                                curLink.set('source', {id: createdCells[subjKey]});
                                var targetID = createdCells[triple.object.object];
                                curLink.set('target', {id: targetID});
                                self.addCell(curLink);
                                break;
                            case 'seeAlso':
                                var elem = self.getCell(createdCells[subjKey]);
                                elem.setUri(triple.object.object);
                                break;
                            case 'label':
                                var elem = self.getCell(createdCells[subjKey]);
                                var strLen = triple.object.object.length;
                                elem.attr('.label/text', triple.object.object.substring(2, strLen - 1));
                                break;
                            default:
                                console.log('error parsing RDF file!');
                                console.log(triple);
                                break;
                        }
                    });
                });

                // align graphs
                self.align(callback);
            }
        });
    },

    // align graph automatically using dagre library
    align: function(callback) {
        var graph = new dagre.graphlib.Graph({ 
            multigraph: true, 
            compound: true, 
            directed: false 
        });

        var graphDefaults = { 
            ranksep: 50, 
            edgesep: 50, 
            nodesep: 50, 
            rankdir: "BT"   // direction = bottom to top
        };

        graph.setGraph(graphDefaults);        
        var self = this;

        // add elements to dagre graph and set parent relationship
        _.each(this.getElements(), function(currentElement) {
            var currentID = currentElement.id;       

            _.each(currentElement.getEmbeddedCells(), function(child) {
                graph.setParent(child.id, currentID);
            });

            graph.setNode(currentID, 
                {width: currentElement.get('size').width, 
                 height: currentElement.get('size').height});
        });

        // add links to dagre graph
        _.each(this.getLinks(), function(link) {
            // check if target or source have embedded cells (otherwise dagre throws error)
            if (self.getCell(link.get('source').id).getEmbeddedCells().length == 0 &&
                self.getCell(link.get('target').id).getEmbeddedCells().length == 0) {

                graph.setEdge(link.get('source').id, link.get('target').id, {});
            }
        });
        
        // align graph
        dagre.layout(graph);

        // assign new position and size to elements
        graph.nodes().forEach(function(v) {
            var posX = graph.node(v).x;
            var posY = graph.node(v).y;

            if (self.getCell(v).getEmbeddedCells().length > 0) {
                posX -= graph.node(v).width / 3;
                posY -= graph.node(v).height / 3;
            }

            // TODO snap to grid
            self.getCell(v).get('position').x = posX;
            self.getCell(v).get('position').y = posY;

            self.getCell(v).translate(1, 1);
            self.getCell(v).resize(graph.node(v).width, graph.node(v).height);
            self.getCell(v).setUri(self.getCell(v).get('uri'));
        });

        // get closest connection ports of target and source for connection
        _.each(this.getLinks(), function(link) {
            var source = self.getCell(link.get('source').id);
            var target = self.getCell(link.get('target').id);

            var sourceCenter = new g.point(source.get('position').x + source.get('size').width / 2,
                                           source.get('position').y + source.get('size').height / 2);
            var targetCenter = new g.point(target.get('position').x + target.get('size').width / 2,
                                           target.get('position').y + target.get('size').height / 2);

            var distance = sourceCenter.distance(targetCenter);

            // get closest port of source element
            source.portLocation.forEach(function(p) {
                var portLocation = new g.point(source.get('position').x + source.get('size').width * p.x,
                    source.get('position').y + source.get('size').height * p.y);

                if (portLocation.distance(targetCenter) < distance) {
                    distance = portLocation.distance(targetCenter);
                    
                    link.set({source: {id: 
                        link.get('source').id, 
                        port: '' + (source.portLocation.indexOf(p) + 1)}
                    });
                }
            });

            distance = sourceCenter.distance(targetCenter);

            // get closest port of target element
            target.portLocation.forEach(function(p) {
                var portLocation = new g.point(target.get('position').x + target.get('size').width * p.x,
                    target.get('position').y + target.get('size').height * p.y);

                if (portLocation.distance(sourceCenter) < distance) {
                    distance = portLocation.distance(sourceCenter);
                    
                    link.set({target: {
                        id: link.get('target').id, 
                        port: '' + (target.portLocation.indexOf(p) + 1)}
                    });
                }
            });
        });

        if (typeof callback !== 'undefined')
            callback();
    },

    toRDFObject: function(uri, withoutPrefix) {
        var prefix = '@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#>.\n' +
                  '@prefix wam: <http://vsr.informatik.tu-chemnitz.de/wam#>.\n';

        var writer = new this.rdfConverter(uri, prefix);
        var self = this;

        if (!withoutPrefix)
            writer.writeTripleObjectPlain('', 'a', 'wam:Description');

        _.each(this.getElements(), function(currentElement) {
            var currentID = currentElement.get('key') + currentElement.cid.substring(1);
            
            // add element type
            writer.writeTripleObjectPlain(currentID, 
                'a', 
                currentElement.get('name'));

            // add label of element
            if (currentElement.attributes.attrs['.label']['text'] != '') {
                var elementLabel = currentElement.attributes.attrs['.label']['text'];
                writer.writeTripleObjectString(currentID, 
                        new RDFSVocabulary().label, 
                        elementLabel);
            }

            // add child elements of current element (eg. elements within realms)
            _.each(currentElement.getEmbeddedCells(), function(child) {
                var childID = child.get('key') + child.cid.substring(1);
                writer.writeTripleObjectUri(currentID, 
                        new WAMVocabulary().contains, 
                        uri + childID);
            });

            // add connections
            _.each(self.getConnectedLinks(currentElement), function(conn) {
                var target = self.getCell(conn.get('target').id);
                
                if (currentElement != target) {
                    var targetID = target.attributes.key + target.cid.substring(1);

                    if (conn.get('type') == 'devs.WAMInvocationConnector') {
                        writer.writeTripleObjectUri(currentID, 
                                new WAMVocabulary().invocation, 
                                uri + targetID);
                    }
                    else if (conn.get('type') == 'devs.WAMTrustRelationshipConnector') {
                        writer.writeTripleObjectUri(currentID, 
                                new WAMVocabulary().trustRelationship, 
                                uri + targetID);
                    }
                    else if (conn.get('type') == 'devs.WAMLegacyRelationshipConnector') {
                        writer.writeTripleObjectUri(currentID, 
                                new WAMVocabulary().legacyRelationship, 
                                uri + targetID);
                    }
                }
            });
            
            // add seeAlso
            if (currentElement.get('uri') != '')
                writer.writeTripleObjectUri(currentID, 
                    new RDFSVocabulary().seeAlso,
                    currentElement.get('uri'));
        });
        
        return writer;
    },

    // serialize graph to rdf
    toRDF: function(uri, withoutPrefix) {
        return this.toRDFObject(uri, withoutPrefix).exportN3();
    },

    addCell: function(cell, options) {
        if (_.isArray(cell)) {
            return this.addCells(cell, options);
        }

        this.get('cells').add(this._prepareCell(cell), options || {});

        // hide connection ports when adding element
        if (!(cell instanceof joint.dia.Link)) {
            $('.inPorts').css("visibility", "hidden");
        }

        return this;
    },
});

