function FileHandler(node) {
    this.node = node
}


FileHandler.prototype.export = function(content, filename) {
    var url = 'data:application/octet-stream;charset=utf8,' + encodeURIComponent(content);
    window.location = url;
};


FileHandler.prototype.open = function(event, callback) {
    if (!FileReader) {
        console.log("FileReader API not supported");
    }

    var file = event.target.files[0]; 

    if (file) {
        var reader = new FileReader();
        reader.onload = function(e) { 
            var contents = e.target.result;
            callback(contents);
        }

        reader.readAsText(file);
    } 
    else { 
        console.log("Failed to load file");
    }
}

// sidebar for editing properties
WAMPropertyView = Backbone.View.extend({
    elementSidebar: null,
    connectionSidebar: null,
    currentElem: null,

    initialize: function(){
        _.bindAll(this, 'render', 
                        'refreshConnectionSidebar', 
                        'refreshElementSidebar', 
                        'renderElementSidebarView', 
                        'renderConnectionSidebarView');
        if (this.model) {
            this.model.on('change', this.render, this);
        }
        this.renderElementSidebarView();
        this.renderConnectionSidebarView();
        this.render();
    },
    render: function(){
        if (this.model.currentElement) {
            if (this.currentElem != this.model.currentElement) {
                this.currentElem = this.model.currentElement;

                if (this.model.currentElement.get('elementType') == 'Element') {
                    this.refreshElementSidebar();
                } 
                else {
                    this.refreshConnectionSidebar();
                }
            }
        }
        else {
            w2ui['WAMEditorLayout'].content('right', '');
        }       
    },
    renderElementSidebarView: function() {
        var self = this;
        elementSidebar = $('#sidebarElementForm').w2form({ 
	        name: 'sidebarElementForm',
	        fields: [
	    	    { 
                    name: 'elementTextInput', 
                    type: 'text', 
                    html: {
                        caption: 'Text:'
                    }
                },
                { 
                    name: 'heightInput', 
                    type: 'int', 
                    options: {
                        arrows: true, 
                        min: 0, 
                       step: 10
                    }, 
                    html: {
                        caption: 'Height:'
                    }
                },
                { 
                    name: 'widthInput', 
                    type: 'int', 
                    options: {
                        arrows: true, 
                        min: 0, 
                        step: 10
                    }, 
                    html: {
                        caption: 'Width:'
                    }
                },
                { 
                    name: 'uriInput', 
                    type: 'text', 
                    html: {
                        caption: 'Uri:'
                    }
                },
                { 
                    field: 'showUriCheckbox', 
                    type: 'checkbox', 
                    html: {
                        caption: 'Hide Uri:'
                    },
                    required: false 
                },

    	    ],
            onChange: function() {
                var label = $('#elementTextInput').val();
                var words = label.split(' ');
                var width = $('#widthInput').val();
                var slice = '';
                var newLabel = '';

                words.forEach(function(w) {
                    if (slice == '' || slice.length + w.length < width / 5) {
                        slice += w + ' ';
                    }
                    else {
                        newLabel += slice + '\n';
                        slice = w;
                    }
                });

                newLabel += slice;
                newLabel = newLabel.trim();

                self.currentElem.attr('.label/text', newLabel);
                self.currentElem.resize($('#widthInput').val(), $('#heightInput').val());
                self.currentElem.setUri($('#uriInput').val());
                if ($('#showUriCheckbox').is(':checked'))
                    $('.uriLabel').css("visibility", "hidden");
                else
                    $('.uriLabel').css("visibility", "visible");
            }
        });

        $(this.el).append(elementSidebar);
    },
    renderConnectionSidebarView: function() {
        var self = this;
        connectionSidebar = $('#sidebarConnectionForm').w2form({ 
	        name: 'sidebarConnectionForm',
	        fields: [
	    	    { 
                    name: 'connectionTextInput', 
                    type: 'text', 
                    html: {
                        caption: 'Text:'
                    }
                }
    	    ],
            onChange: function() {
                // update label of connection line
                if (self.currentElem) {
                    self.currentElem.label(0, { attrs: { text: { text: $('#connectionTextInput').val()}}});            
                }
            }
        });

        $(this.el).append(connectionSidebar);
    },
    refreshElementSidebar: function() {
        var elemData = this.currentElem.attributes;
        elementSidebar.record['elementTextInput'] = this.currentElem.attr('.label/text');  
        elementSidebar.record['heightInput'] = elemData.size.height;
        elementSidebar.record['widthInput'] = elemData.size.width;
        elementSidebar.record['uriInput'] = elemData.uri;
        w2ui['WAMEditorLayout'].content('right', elementSidebar);
    },
    refreshConnectionSidebar: function() {
        var elemData = this.currentElem.attributes;
        connectionSidebar.record['connectionTextInput'] = elemData.labels[0].attrs.text.text;
        w2ui['WAMEditorLayout'].content('right', connectionSidebar);
    }
});

WAMPaper = joint.dia.Paper.extend({
    // selected connection type
    currentConnection: WAMInvocationConnector,

    initialize: function() {
        // select current element or connection
        this.on('cell:pointerdown', function(cellView, evt, x, y) {
            if (this.model.currentElement != null)
                this.model.currentElement.deselect();
            
            this.model.currentElement = cellView.model;
            cellView.model.select();

            if (this.model.currentElement.get('elementType') == 'Element') {
                if (this.model.currentElement.get('parent')) {
                    this.model.getCell(this.model.currentElement.get('parent')).unembed(this.model.currentElement);
                }
            }
        });

        // remove links that have not target
        this.model.on('batch:stop', function () {
            var links = paper.model.getLinks();
            _.each(links, function (link) {
                var source = link.get('source');
                var target = link.get('target');
                if (source.id === undefined || target.id === undefined) {
                    link.remove();
                }
            });
        });

        joint.dia.Paper.prototype.initialize.apply(this, arguments);

        this.reset();
    },

    reset: function() {
        this.model.resetCells();

        _.each(this.options.startup, this.addPiece, this);
    }
});



var filehandler = new FileHandler();
