// initialize drawing area
WAMEditor.prototype.createPaper = function(node) {
    var graph = new WAMGraph;

    if (!this.readonly) {
        var propertySidebarElement = document.createElement('div');
        propertySidebarElement.setAttribute('id', 'WAMEditorSidebar');
        $("#WAMEditorLayout").append(propertySidebarElement);

        var sidebarElementForm = document.createElement('div');
        sidebarElementForm.setAttribute('id', 'sidebarElementForm');
        propertySidebarElement.appendChild(sidebarElementForm);

        var sidebarConnectionForm = document.createElement('div');
        sidebarConnectionForm.setAttribute('id', 'sidebarConnectionForm');
        propertySidebarElement.appendChild(sidebarConnectionForm);

        propertyView = new WAMPropertyView({
            el: propertySidebarElement,
            model: graph   
        });
    }
    
    w2ui['WAMEditorLayout'].content('main', 
                '<div id="WAMEditorPaper"></div><input type="file" id="openJsonFileInput" />' +
                '</div><input type="file" id="openRdfFileInput" />');

    var self = this;
    
    paper = new WAMPaper({
        el: $('#WAMEditorPaper'),
        width: $('#WAMEditorPaper').width(),
        height: 400,
        model: graph,
        gridSize: 5,
        snapLinks: { radius: 20 },
        defaultLink: function() {
            return new this.currentConnection()
        },
        interactive: !self.readonly
    });

    if (this.readonly)
        paper.$el.css('pointer-events', 'none');


    // when the dragged element is dropped over a realm, let it become a child
    paper.on('cell:pointerup', function(cellView, evt, x, y) {
        var currentElement = paper.model.currentElement;
        if (currentElement && currentElement.attributes.elementType == "Element") {
            var cellViewsBelow = paper.findViewsFromPoint(currentElement.getBBox().center());

            if (cellViewsBelow.length) {
                var cellViewBelow = null;
                var elementBelowFound = false;

                // get realm element directly beneath current element
                for (var i = 0; i < cellViewsBelow.length; i++) {
                    if (currentElement.id != cellViewsBelow[i].model.id) {
                        if (!cellViewBelow || (currentElement.attributes.z > cellViewsBelow[i].model.attributes.z && 
                            !elementBelowFound && cellViewsBelow[i].model.attributes.type == 'devs.WAMSecurityRealmElement')) {
                            cellViewBelow = cellViewsBelow[i];
                            elementBelowFound = true;
                        }
                        else if (elementBelowFound && currentElement.attributes.z > cellViewsBelow[i].model.attributes.z &&
                            cellViewsBelow[i].model.attributes.z > cellViewBelow.model.attributes.z &&
                            cellViewsBelow[i].model.attributes.type == 'devs.WAMSecurityRealmElement') {
                            cellViewBelow = cellViewsBelow[i];
                        }
                    }
                }
        
                // prevent recursive embedding
                if (cellViewBelow && 
                    cellViewBelow.model.attributes.type == 'devs.WAMSecurityRealmElement' &&
                    cellViewBelow.model.get('parent') !== currentElement.id &&
                    currentElement.attributes.z > cellViewBelow.model.attributes.z) {
                        cellViewBelow.model.embed(currentElement);
                }
            }
        }
    });

    var self = this;

    // init input for JSON files
    $('#openJsonFileInput').change(function(event) {
        filehandler.open(event, function(content) {
            paper.model.fromJSON(JSON.parse(content));
            $('.inPorts').css('visibility', 'hidden');

            // if connections tab is currently active show eligible source elements
            if (w2ui['tabs'].active == 'tab2') {
                self.showEligibleSourceElementsForConnection();
                $('.inPorts').css('visibility', 'visible');
            }  
        });
    });

    // init input for RDF files (N3 format)
     $('#openRdfFileInput').change(function(event) {
        filehandler.open(event, function(content) {
            var callback = function() {
                $('.inPorts').css('visibility', 'hidden');
                // if connections tab is currently active show eligible source elements
                if (w2ui['tabs'].active == 'tab2') {
                    self.showEligibleSourceElementsForConnection();
                    $('.inPorts').css('visibility', 'visible');
                }  
            }

            paper.model.fromRDF(content, callback);
        });
    });

    var dropping = false;

    paper.model.on('change:source change:target', function(link) {
        if (w2ui['tabs'].active == 'tab2') {
            if (link.get('source').id && link.get('target').id) {
                paper.on('cell:pointerup', function(cellView, evt, x, y) {
                    if (dropping) {
                        self.showEligibleSourceElementsForConnection();
                        dropping = false;   
                        self.onchange();     
                    }
                });
            }
            else {
                var sourceElement = paper.model.getCell(link.get('source').id);
                self.showEligibleTargetElementsForConnection(sourceElement); 
                dropping = true;       
            }
        }
    });

    paper.model.on('remove', function(cell) {
        if (paper.model.currentElement != null && 
            paper.model.get('elementType') != 'Element') {
            paper.model.currentElement = null;
            //propertyView.render();
        }
    });

    paper.model.on('remove ChangedWAMElement', function(evt) {
        self.onchange();
    });

    paper.model.on('add', function(el) {
        if (el.get('elementType') == 'Element')
            self.onchange();
    });
}


// highlight elements that are allowed to be connected with the selected connector type according to ontology
WAMEditor.prototype.showEligibleSourceElementsForConnection = function() {
    var key = new paper.currentConnection({}).get('name');

    _.each(paper.model.getElements(), function(element) {
        if (key in WAMOntology[element.get('name')]) {
            element.highlight();
        } 
        else {
            element.unhighlight();
        } 
    });
}

WAMEditor.prototype.enableElements = function() {
    _.each(paper.model.getElements(), function(element) {
        element.unhighlight();
    });
}

WAMEditor.prototype.showEligibleTargetElementsForConnection = function(sourceElement) {
    var key = new paper.currentConnection({}).get('name');

    _.each(paper.model.getElements(), function(element) {
        element.unhighlight();

        if (WAMOntology[sourceElement.get('name')][key]) {
            var eligibleElements = WAMOntology[sourceElement.get('name')][key];
        
            _.each(eligibleElements, function(objType) {
                if (element instanceof objType)
                    element.highlight();
            });
        }
    });
}

