// add tabs for editing elements and connections
WAMEditor.prototype.createTabContainer = function(node) {
    var tabsElement = document.createElement('div');
    tabsElement.setAttribute('id', 'WAMEditorTabs');

    if (!this.readonly) {
        node.append(tabsElement);
    }

    var tabsContentElement = document.createElement('div');
    tabsContentElement.setAttribute('id', 'WAMEditorTabsContent');
    node.append(tabsContentElement);

    if (!this.readonly) {
        this.createElementToolbar(node);
        this.createConnectionToolbar(node);
    }
    
    var self = this;

    $('#WAMEditorTabs').w2tabs({
       	name: 'tabs',
       	active: 'tab1',
       	tabs: [
       		{ 
              id: 'tab1', 
              name:'element', 
              caption: 'Elements', 
              onClick: function() {
                  $('.inPorts').css("visibility", "hidden");
                  self.enableElements();
              }
          },
       		{ 
              id: 'tab2', 
              name:'connection', 
              caption: 'Connections', 
              onClick: function() {
                  $('.inPorts').css("visibility", "visible");
                  self.showEligibleSourceElementsForConnection();
              }
          }
        ],
        onClick: function (event) {
            $('.toolbar').each(function() {
                $(this).css('display', 'none');
            });

            if (event.tab.name == 'connection') {
                $('#WAMEditorConnectionToolbar').css('display', 'block');
            }
            else {
                $('#WAMEditorElementToolbar').css('display', 'block');
            }
        }
    });
}
